<section class="job-breadcrumb">    <div class="container">        <div class="row">            <div class="col-md-6 col-sm-7 co-xs-12 text-left">                <h3>Log in</h3>            </div>            <div class="col-md-6 col-sm-5 co-xs-12 text-right">                <div class="bread">                    <ol class="breadcrumb">                        <li><a href="#">Home</a></li>                        <li class="active">Log in</li>                    </ol>                </div>            </div>        </div>    </div></section><section class="login-page-2 parallex full-page">    <div class="container">        <div class="row">            <div class="col-md-12 col-sm-12 col-xs-12">                <div class="login">                    <form action="" id="signinform" method="post">                        <div class="login_title">                            <img src="<?php echo ASSETS_ROOT ?>images/logo.png" alt="logo" class="img-responsive center-block">                        </div>                        <div class="login_fields">                            <div class="login_fields__user">                                <div class="icon">                                    <i class="icon-profile-male"></i>                                </div>                                <input name="email" placeholder="User email" type="text">                            </div>                            <div class="login_fields_password">                                <div class="icon">                                    <i class="icon-lock"></i>                                </div>                                <input name="password" placeholder="Password" type="password">                            </div>                            <div class="login_fields_submit">                                <a id="signin_btn" class="btn btn-default login-header-btn" ><i class="fa fa-sign-in"></i> Log in</a>&emsp;                                <a class="btn btn-default login-header-btn"  href="<?php echo site_url('auth/checktype'); ?>"><i class="fa fa-user-plus"></i> Sign up</a>                            </div>                    </form>                </div>            </div>        </div>    </div></section><script type="text/javascript">    $(".full-page").height($(window).height());    $(window).resize(function () {        $(".full-page").height($(window).height());    });    $(document).ready(function () {        $("#signin_btn").click(function () {            $("#signinform").validate({                rules: {                    email: {                        required: true,                        email: true                    },                    password: {                        required: true,                        minlength: 5                    }                },                messages: {                    password: {                        required: "Please enter Password",                        minlength: "Length more 5 "                    },                    email: {                        required: "Please enter email address",                        email: "Format Email address"                    }                }            });            if ($("#signinform").valid() == false) {                $("#signinform").valid();                return false;            }            var email = $("input[name=email]").val();            var password = $("input[name=password]").val();            $.post(                '<?php echo site_url('auth/login') ?>',                {email: email, password: password},                function (result) {                    if (result.status == "ok") {                        location.href = "<?php echo site_url('home')?>";                    } else {                        alert("email or password fail!");                        return false;                    }                }, 'json'            );        });    });</script></div></body></html>