<script>    $(document).ready(function () {        $("#signin_btn").click(function () {            $("#signinform").validate({                rules: {                    email: {                        required: true,                        email: true                    },                    password: {                        required: true,                        minlength: 5                    }                },                messages: {                    password: {                        required: "Please enter Password",                        minlength: "Length more 5 "                    },                    email: {                        required: "Please enter email address",                        email: "Format Email address"                    }                }            });            if ($("#signinform").valid() == false) {                $("#signinform").valid();                return false;            }            var email = $("input[name=email]").val();            var password = $("input[name=password]").val();            $.post(                '<?php echo site_url('auth/login') ?>',                {email: email, password: password},                function (result) {                    if (result.status == "ok") {                        location.href = "<?php echo site_url('job/post/post')?>";                    }else{                        alert("email or password fail!");return false;                    }                },'json'            );        });    });</script><section class="job-breadcrumb">    <div class="container">        <div class="row">            <div class="col-md-6 col-sm-7 co-xs-12 text-left">                <h3>Login Page</h3>            </div>            <div class="col-md-6 col-sm-5 co-xs-12 text-right">                <div class="bread">                    <ol class="breadcrumb">                        <li><a href="#">Home</a> </li>                        <li class="active">login</li>                    </ol>                </div>            </div>        </div>    </div></section><section class="login-page light-blue">    <div class="container">        <div class="row">            <div class="col-md-12 col-sm-12 col-xs-12">                <div class="login-container">                    <div class="loginbox">                        <form action="<?php echo site_url("job/auth/login/"); ?>" id="signinform" method="post">                            <div class="form-group">                                <label>Email: <span class="required">*</span></label>                                <input placeholder="" name="email" class="form-control" type="email">                            </div>                            <div class="form-group">                                <label>Password: <span class="required">*</span></label>                                <input placeholder="" name="password" class="form-control" type="password">                            </div>                            <div class="loginbox-submit">                                <input type="button" id="signin_btn" class="btn btn-default btn-block" value="Login">                            </div>                            <div class="loginbox-signup">                                <a href="<?php echo site_url('auth/register') ?>">Sign Up With Email</a>                            </div>                        </form>                    </div>                </div>            </div>        </div>    </div></section>